function isAdminByCard(card_uid){
  var cs = members().data;
  for(var i = 0; i < cs.length; i++){
    if(cs[i].card_uid != card_uid) continue;
    if(!cs[i].is_admin) return null;
    return cs[i];
  }
  return null;
}

function isAdmin(stripeid){
  var cs = members().data;
  for(var i = 0; i < cs.length; i++){
    if(cs[i].stripeid != stripeid) continue;
    return cs[i].is_admin;
  }
  return false;
}

